# This is LOCK script. Customer has to set this script to his account to be able to get money from the bank

{-# STDLIB_VERSION 3 #-}
{-# CONTENT_TYPE EXPRESSION #-}
{-# SCRIPT_TYPE ACCOUNT #-}

# Bank dApp address
let BANK = base58'3JGVZkdiFoYdSDtcNESA1SvPiakQ6KdFAyP'


let TARGETHEIGHT = match( getInteger(Address(BANK), toBase58String(tx.sender.bytes) + "_target")){
    case x: Unit => 0
    case x: Int => x
}
let status = match( getString(Address(BANK), toBase58String(tx.sender.bytes) + "_status")){
    case x: Unit => "unset"
    case x: String => x
}

# If it is time to pay and money has been borrowed and not returned, we only allow InvokeScript on bank dApp. Anyone can call this dapp
if (height > TARGETHEIGHT && status == "borrowed") then {
    match (tx){
        case t: InvokeScriptTransaction => addressFromRecipient(t.dApp).bytes == BANK
        case _ => false
    }
# Otherwise if returned allow all
}else if(status == "returned" || status == "canceled") then {
    sigVerify(tx.bodyBytes, tx.proofs[0], tx.senderPublicKey)
# In any other case forbid SetScript
}else {
    match (tx){
        case t: SetScriptTransaction => false
        case _ => sigVerify(tx.bodyBytes, tx.proofs[0], tx.senderPublicKey)
    }
}

{-# STDLIB_VERSION 3 #-}
{-# CONTENT_TYPE DAPP #-}
{-# SCRIPT_TYPE ACCOUNT #-}

@Callable(i)
func approveCredit(client: String, amount: Int, target: Int, lockHash: ByteVector) = {
    if (this == i.caller && !isDefined(getBoolean(this, client + "_status"))) then {
        WriteSet([
            DataEntry(client + "_status", "approved"),
            DataEntry(client + "_amount", amount),
            DataEntry(client + "_target", target),
            DataEntry(client + "_lockScriptId", lockHash)
        ])
    }else {
        throw("Credit for " + client + " has already been approved or caller is not dApp owner")
    }

}

@Callable(i)
func getMoney() = {

    let callerStr = toBase58String(i.caller.bytes)

    let status = match( this.getString(callerStr + "_status")){
        case x: Unit => "unset"
        case x: String => x
    }

    let lockTxId = this.getString(callerStr + "_lockScriptId").extract()
    let lockTx = transactionHeightById(fromBase58String(lockTxId))

    let isLockSet = match (lockTx) {
        case t: Int => true
        case _ => throw("Lock has not been set")
    }

    let amount = this.getInteger(callerStr + "_amount").extract()

    if (status == "approved" && isLockSet) then {
        ScriptResult(
            WriteSet([
                DataEntry(callerStr + "_status", "borrowed")
            ]),
            TransferSet([ScriptTransfer(i.caller, amount, unit)])
        )
    }else throw("Credit was not approved or money has already been taken")

}

@Callable(i)
func returnMoney() = {
    let callerStr = toBase58String(i.caller.bytes)
    let status = match( this.getString(callerStr + "_status")){
        case x: Unit => "unset"
        case x: String => x
    }

    let pmt = extract(i.payment)

    let amountToReturn = getInteger(this, callerStr + "_amount")
    if(status != "borrowed") then throw("Cannot return, nothing was borrowed")
    else if (isDefined(pmt.assetId)) then throw("Can return only ACRYL at the moment")
    else if (amountToReturn != pmt.amount) then throw("Should return borrowed amount")
    else {
        WriteSet([
            DataEntry(callerStr + "_status", "returned")
        ])
    }
}

@Callable(i)
func cancelContract() = {
    let callerStr = toBase58String(i.caller.bytes)
    let status = match( this.getString(callerStr + "_status")){
        case x: Unit => "unset"
        case x: String => x
    }
    if(status == "approved" || status == "unset") then WriteSet([DataEntry(callerStr + "_status", "canceled")])
    else {throw("Cannot cancel credit. Money has already been taken")}
}

@Verifier(tx)
func verify() = {
    match(tx) {
        case t:DataTransaction => false
        case t:SetScriptTransaction => false
        case _ => sigVerify(tx.bodyBytes, tx.proofs[0], tx.senderPublicKey)
    }
}

const acr = 10 ** 8;

describe('Bank Dapp test Suite', () => {

    let lockTx;
    let removeLockTx;
    let paymentAmount = 0.4 * acr

    before(async function() {
        this.timeout(0);

        await setupAccounts({bank: 0.5 * acr, client: 0.1 * acr});

        const compiledDapp = compile(file('bank_dapp.ride'))
        const bankAddress = address(accounts.bank)
        const lockScript = file('account_lock.ride')
            .replace(`base58'3JGVZkdiFoYdSDtcNESA1SvPiakQ6KdFAyP'`, `base58'${bankAddress}'`)

        const compiledLock = compile(lockScript)

        lockTx = setScript({script: compiledLock}, accounts.client)
        removeLockTx = setScript({script: null, additionalFee: 400000}, accounts.client)
        const dAppTx = setScript({script: compiledDapp}, accounts.bank)
        await broadcast(dAppTx)
        await waitForTx(dAppTx.id)
        console.log('Scrips were deployed')
    })

    it('Bank approves credit for customer', async function(){
        const invTx = invokeScript({
            additionalFee: 400000,
            dApp: address(accounts.bank),
            call: {
                function: "approveCredit",
                args: [{
                    type: "string",
                    value: address(accounts.client)
                }, {
                    type: "integer",
                    value: paymentAmount
                }, {
                    type: "integer",
                    value: 0
                }, {
                    type: "string",
                    value: lockTx.id
                }]
            }
        }, accounts.bank)

        await broadcast(invTx)
        await waitForTx(invTx.id)
    })

    it('Customer fails to get money without lock', async function(){
        const invTx = invokeScript({
                additionalFee: 500000,
                dApp: address(accounts.bank),
                call: {
                    function: "getMoney"
                }
            }, accounts.client)
        await expect(broadcast(invTx)).rejectedWith()
    })

    it('Customer sets lock and gets money', async function(){
        this.timeout(0)

        await broadcast(lockTx)
        await waitForTx(lockTx.id, {timeout: 1000000})

        const invTx = invokeScript({
            additionalFee: 500000,
            dApp: address(accounts.bank),
            call: {
                function: "getMoney"
            }
        }, accounts.client)
        await broadcast(invTx)
        await waitForTx(invTx.id, {timeout: 1000000})
    })

    it('Customer fails to remove lock', async function(){
        await expect(broadcast(removeLockTx)).rejectedWith()
    })

    it('Customer returns money and removes lock from his account', async function(){
        this.timeout(0)
        const invTx = invokeScript({
            additionalFee: 500000,
            dApp: address(accounts.bank),
            call: {
                function: "returnMoney",
                args: []
            },
            payment:[{
                amount: paymentAmount
            }]
        }, accounts.client)
        await broadcast(invTx);
        await waitForTx(invTx.id, {timeout: 1000000});
        await broadcast(removeLockTx)
    })
})

{-# STDLIB_VERSION 3 #-}
{-# CONTENT_TYPE DAPP #-}
